#!/bin/bash
PWD="`pwd`"
CWD=$(cd "$(dirname "$0")"; pwd)
FOLDER=$1
ABS_FOLDER="${CWD}/${FOLDER}"
EXEC_COMMAND="exec_command"

echo "Generate directy list ${ABS_FOLDER}"

for DIR in $(find ${ABS_FOLDER} -type d | grep -v ".git"); do
  echo "enter $DIR"
  RELATIVE_DIR=${DIR/$ABS_FOLDER//}
  #echo $RELATIVE_DIR
  (
    echo -e '<!doctype html>\n<html>\n\t<head>\n\t\t<style>\n  h1 {\n    border-bottom: 1px solid #c0c0c0;\n    margin-bottom: 10px;\n    padding-bottom: 10px;\n    white-space: nowrap;\n  }\n\n  table {\n    border-collapse: collapse;\n  }\n\n  .monospace{\nfont-family:monospace;\n}\ntr.header {\n    font-weight: bold;\n  }\n\n  td.detailsColumn {\n    padding-left: 2em;\n    text-align: right;\n    white-space: nowrap;\n  }\n\n  a.icon {\n    padding-left: 1.5em;\n    text-decoration: none;\n  }\n\n  a.icon:hover {\n    text-decoration: underline;\n  }\n\n  a.file {\n    background : url("data:image/png;base64, ") left top no-repeat;\n  }\n\n  a.dir {\n    background : url("data:image/png;base64, ") left top no-repeat;\n  }\n\n  a.up {\n    background : url("data:image/png;base64, ") left top no-repeat;\n  }\n\t\t</style>\n\t'
    echo -e "<title>Index of ${RELATIVE_DIR}</title>"
    echo -e '</head>\n<body>'
    echo -e "<h1 id=\"header\">Index of ${RELATIVE_DIR}</h1>"
    echo -e '<table id="table">\n\t<tbody>\n\t    <tr class="header">\n                <td i18n-content="headerName">File</td>\n                <td class="detailsColumn">Size</td>\n                <td class="detailsColumn">Date Modified</td>\n                <td class="detailsColumn">MD5</td>\n            </tr>\n        </tbody>'
    ls -1pash "${DIR}" --group-directories-first --full-time \
        | grep -v "^\./$" \
        | grep -v "index\.html$" \
        | grep -v ".git/$" \
        | awk '{if($1 == "total") {\
#printf "Total	[%s]\n",$2
}\
else if ($10 ~ /^\.\/$/) {\
# current folder
}\
else if ($10 ~ /^\.\.\/$/) {\
# up folder
if (relative_dir ~ /^\/$/) {\
}else{\
printf "\t<tr>\n\t\t<td><a class=\"icon up\" href=\"../\">[parent directory]</a></td>\n\t\t<td class=\"detailsColumn\"></td>\n\t\t<td class=\"detailsColumn\"></td>\n\t\t<td class=\"detailsColumn\"></td>\n\t</tr>\n"\
}\
}\
else if ($10 ~ /\/$/) {\
# directory
printf "\t<tr>\n\t\t<td><a class=\"icon dir\" href=\"%s\">%s</a></td>\n\t\t<td class=\"detailsColumn\"></td>\n\t\t<td class=\"detailsColumn\">%s %s</td>\n\t\t<td class=\"detailsColumn\"></td>\n\t</tr>\n",$10,$10,$7,substr($8,0,9)\
}\
 else {\
printf "\t<tr>\n\t\t<td><a class=\"icon file\" href=\"%s\">%s</a></td>\n\t\t<td class=\"detailsColumn\">%s</td>\n\t\t<td class=\"detailsColumn\">%s %s</td>\n\t\t<td class=\"detailsColumn monospace\">\n%s md5sum %s/%s\n</td>\n\t</tr>\n",$10,$10,$1,$7,substr($8,0,9),command,dir,$10\
}\
}' dir=$DIR relative_dir=$RELATIVE_DIR command=$EXEC_COMMAND
    echo -e '</table>\n</body>\n</html>'
  ) | while read line; do #while IFS= read line; do 
    if [[ "$line" =~ "$EXEC_COMMAND" ]];then
        commandLine=($line)
        command="${commandLine[1]}"
        case "$command" in
            "md5sum")
                fileName="${commandLine[2]}"
                md5Result=($(md5sum $fileName))
                echo "${md5Result[0]}"
                ;;
            *)
                echo ""
                ;;
        esac
    else
        echo "$line"
    fi
    done > "${DIR}/index.html"
done
